const bikewrapper = select('#bikewrapper');

function init() {

  enterView({
    selector: bikewrapper.nodes(),
    offset: 0.3,
    enter: el => {
      drawChart(el);
    },
    once: true
  });
}

init();

function reDraw () {
  drawChart();
}

window.addEventListener('resize', reDraw);

if (window.innerWidth < 768) window.removeEventListener('resize', reDraw);